import { Component } from '@angular/core';
import { Router, RouterModule } from '@angular/router';
import { StudentGuideService } from '../student-guide.service';



@Component({
  selector: 'app-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.scss']
})
export class SigninComponent {
  data: any = {};
  // email: any = {};
  // password: any = {};


  constructor(private service: StudentGuideService, private router : Router ) { }

  submitForm() {
    this.service.login(this.data).subscribe(response => {
     alert("Sign In completed Successfully");
    },error => {

      if(error.error.text==='login success') {
        alert(error.error.text);
        this.router.navigate(['/home']);

      } else {
        alert("Invalid Credentials");
        this.router.navigate(['/singin']);
      }
    });
  }
  }
